from launch import LaunchDescriptionfrom launch_ros.actions import Nodedef generate_launch_description():    octomap_node = Node(        package='octomap_server2',        executable='octomap_server',        name='octomap_server',        output='screen',        remappings=[            ('cloud_in', '/rgbd_camera/points')        ],        parameters=[{            'resolution': 0.05,            'frame_id': 'odom',            'base_frame_id': 'base_link',            'filter_ground': True,            'ground_filter_distance': 0.04,            'ground_filter_angle': 0.15,            'ground_filter_plane_distance': 0.07        }]    )    return LaunchDescription([        octomap_node    ])