#!/usr/bin/env python3from launch import LaunchDescriptionfrom launch_ros.actions import Nodedef generate_launch_description():    return LaunchDescription([        Node(            package='octomap_server2',            executable='octomap_server',            name='octomap_server_filtered',            output='screen',            # 1) Remap Jetbot RGBâ€‘D point cloud to cloud_in            #    (change topic here if your camera uses a different one)            remappings=[                ('cloud_in', '/rgbd_camera/points'),            ],            # 2) Set fixed parameters with ground filtering enabled            parameters=[{                # Basic map settings                'resolution': 0.10,          # 10 cm voxels, matches maze map                'frame_id': 'odom',           # OctoMap root frame                'base_frame_id': 'base_link',                'incremental_2D_projection': True,                # Visualisation / projection                'height_map': True,                'colored_map': True,                'color_factor': 0.8,                'compress_map': True,                'incremental_2D_projection': True,                # Ground filtering (ON)                'filter_ground': True,                'filter_speckles': True,                'ground_filter/distance': 0.04,                'ground_filter/angle': 0.15,                'ground_filter/plane_distance': 0.20,                # Sensor model                'sensor_model/max_range': 10.0,                'sensor_model/hit': 0.7,                'sensor_model/miss': 0.4,                'sensor_model/min': 0.12,                'sensor_model/max': 0.97,                # Colours (optional, for RViz)                'color/r': 0.0,                'color/g': 0.0,                'color/b': 1.0,                'color/a': 1.0,                'color_free/r': 0.0,                'color_free/g': 0.0,                'color_free/b': 1.0,                'color_free/a': 1.0,                # Publish free space voxels if wanted                'publish_free_space': False,            }]        )    ])